//DEPENDENCIES
import { Observable } from 'rxjs/Rx';
import { Http, RequestOptions, Headers, Response } from '@angular/http';
import { Injectable } from '@angular/core';
import 'rxjs/add/operator/map';

// SECURITY
import { AuthenticationService } from '../../security/authentication.service';

// MODEL
import { Role } from '../../domain/sw-app_db/role';

// CONFIG
import { config } from "../../../config/properties";

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../Role.service.ts
 */
 
/*
 * SCHEMA DB Role
 * 
	{
		description: {
			type: 'String'
		},
		name: {
			type: 'String', 
			required : true
		},
		//RELATIONS
		
		
		//EXTERNAL RELATIONS
		
		user_role: [{
			type: Schema.ObjectId, 
			required : true,
			ref : "User"
		}],
		
	}
 * 
 */
@Injectable()
export class RoleBaseService {

    contextUrl:string = config.host + "/roles";
    constructor(
        protected http: Http,
        protected authenticationService: AuthenticationService) {
        
    }

//CRUD METHODS
	
	/**
     * Remove one item by id
     */
     remove(id: string): Observable<void> {
        return this.http
            .delete(this.contextUrl + "/" + id)
            .map(response => null);
    }
	
    /**
     * Get one item by id
     */
	
	get(id: string): Observable<Role> {
        return this.http
            .get(this.contextUrl + "/" + id)
            .map(response => response.json() as Role)
    }

	
    /**
     * Get list of items
     */
	
	list(): Observable<Role[]> {
        return this.http
            .get(this.contextUrl)
            .map(response => response.json() as Role[])
    }
	
    /**
     * Update item
     */
	update(item: Role): Observable<Role> {
        return this.http
            .post(this.contextUrl + '/' + item._id, item)
            .map(response => response.json())
    }



}
